  --badge-outline: rgba(0,0,0, .04);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -6; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .015);
  --elevate-2: rgba(0,0,0, .04);

  /* Refined neutral palette - professional and sophisticated */
  --background: 210 12% 98%;
  --foreground: 215 20% 15%;

  /* Subtle borders for enterprise elegance */
  --border: 210 10% 88%;

  /* Clean card backgrounds */
  --card: 0 0% 100%;
  --card-foreground: 215 20% 15%;
  --card-border: 210 8% 92%;

  /* Sidebar neutrals */
  --sidebar: 210 10% 97%;
  --sidebar-foreground: 215 20% 15%;
  --sidebar-border: 210 8% 90%;
