  --shadow-sm: 0px 2px 4px -1px rgba(0, 0, 0, 0.06), 0px 4px 6px -1px rgba(0, 0, 0, 0.1);
  --shadow: 0px 4px 6px -1px rgba(0, 0, 0, 0.1), 0px 2px 4px -1px rgba(0, 0, 0, 0.06);
  --shadow-md: 0px 6px 12px -2px rgba(0, 0, 0, 0.08), 0px 3px 7px -3px rgba(0, 0, 0, 0.1);
  --shadow-lg: 0px 10px 20px -5px rgba(0, 0, 0, 0.1), 0px 4px 10px -3px rgba(0, 0, 0, 0.08);
